[asy]
unitsize(0.3 cm);

real func(real x) {
  real y;
  if (x >= -3 && x <= 0) {y = -2 - x;}
  if (x >= 0 && x <= 2) {y = sqrt(4 - (x - 2)^2) - 2;}
  if (x >= 2 && x <= 3) {y = 2*(x - 2);}
  return(y);
}

int i, n;

for (i = -8; i <= 8; ++i) {
  draw((i,-8)--(i,8),gray(0.7));
  draw((-8,i)--(8,i),gray(0.7));
}

draw((-8,0)--(8,0),Arrows(6));
draw((0,-8)--(0,8),Arrows(6));

label("$x$", (8,0), E);
label("$y$", (0,8), N);

draw(graph(func,-3,3),red);

label("$y = f(x)$", (4,-3), UnFill);
[/asy]

Which is the graph of $y = f(2x + 1)$?

[asy]
unitsize(0.3 cm);

picture[] graf;
int i, n;

real func(real x) {
  real y;
  if (x >= -3 && x <= 0) {y = -2 - x;}
  if (x >= 0 && x <= 2) {y = sqrt(4 - (x - 2)^2) - 2;}
  if (x >= 2 && x <= 3) {y = 2*(x - 2);}
  return(y);
}

real funca(real x) {
  return(func(2*x + 1));
}

for (n = 1; n <= 5; ++n) {
  graf[n] = new picture;
