pair P, Q;

P = (1.7,0.8);
Q = -P;

draw((-2,0)--(2,0));
draw((0,-2)--(0,2));
draw(P--Q, dashed);

dot("$(a,b)$", P, NE);
dot("$(-a,-b)$", Q, SW);
[/asy]

