//import geometry;
draw((0,0)--(9,0)--(9,2)--(4,2)--(4,5)--(0,5)--(0,0));

label("4 yds",(2,5),N);
label("3 yds",(4,3.5),E);
label("5 yds",(6.5,2),N);
label("2 yds",(9,1),E);

[/asy]

