[asy]
unitsize(0.5 cm);

pair A, B;
pair[] C;

A = (2,2);
B = (5,1);
C[1] = rotate(60,A)*(B);
C[2] = rotate(60,B)*(A);

draw(A--C[1]--B--C[2]--cycle);
draw(A--B);

dot("$-5 + 3i$", A, W);
dot("$8 - i$", B, E);
dot("$c_1$", C[1], N);
dot("$c_2$", C[2], S);
[/asy]
