path B = reflect ( dir(72), dir(144)) * A;
path C = reflect( dir(72), dir(0))* A;
draw(rotate(18) *A);
draw(rotate(18) *B);
draw(rotate(18) *C);
label("?", (0,1.5), N);
[/asy]

