[asy]
draw((0,0)--(25,0)--(25,15)--(0,15)--cycle,linewidth(1));
draw((0,0)--(25,15),linewidth(1));
draw((7.2,4.3)--(0,15),linewidth(1));
dot((12.5,7.5));
label("$M$",(12.5,7.5),N);
label("$A$",(0,0),S);
label("$B$",(0,15),N);
label("$C$",(25,15),N);
label("$D$",(25,0),S);
label("$X$",(7.2,4.3),SE);
[/asy]

